(function () {

	if (!window.$) {
		//Test for ie8
	    var IE = (function() { 
	        if (document.documentMode) {
	            return document.documentMode;
	        }
	        return false;
	    })();

	    //Load jquery
        Modernizr.load([
            {
                test: IE == 8,
                yep: 'libraries/jquery.js',
                nope: 'libraries/jquery.v2.js',
                complete: loadAdapt
            }
        ]);

	} else loadAdapt();

	function loadAdapt() {
		require.config({
			baseUrl: "./",
			paths: {
				libraries: "adapt/js/libraries",
				core: "adapt/js/core",
				bundles: "adapt/js/bundles",
				templates: "adapt/js/templates",
			},
			map: {
				'*': {
					"a11y": "jquery.a11y",
		            'coreJS/libraries/bowser': 'bowser',
		            'components/components': 'bundles',
		            'extensions/extensions': 'bundles',
		            'theme/theme': 'bundles',
		            'menu/menu': 'bundles',
		        }
	        },
			shim: {
				'templates': [
					'libraries'
				],
				'core': [
					'libraries',
					'templates'
				],
				'bundles': [
					'libraries',
					'core',
					'templates'
				]
			}
		});

		require([
			'bundles'
		], function() {});
	}

})();